import { createContext, useState, useEffect } from 'react';
import jwt_decode from "jwt-decode";
import { useNavigate } from 'react-router-dom'
import { w3cwebsocket as W3CWebSocket } from 'websocket';

const AuthContext = createContext();

export default AuthContext;

export const AuthProvider = ({ children }) => {
  const navigate = useNavigate();

  const [authToken, setAuthTokens] = useState(() => localStorage.getItem('authToken') ? JSON.parse(localStorage.getItem('authToken')) : null);
  const [user, setUser] = useState(() => localStorage.getItem('authToken') ? jwt_decode(localStorage.getItem('authToken')) : null);
  const [loading, setLoading] = useState(true);
  const url = `ws://localhost:8000/ws/socket-server/`;
  const [webSocket, setWebSocket] = useState(null);

  const loginUser = async (e) => {
    e.preventDefault();

    const response = await fetch('http://localhost:8000/users/token/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ 'username': e.target.username.value, 'password': e.target.password.value })
    });

    const data = await response.json();

    if (response.status === 200) {
      setAuthTokens(data);
      setUser(jwt_decode(data.access));

      if (!webSocket) {
        const ws = new W3CWebSocket(url + "?token=" + String(data.access));
        ws.onopen = () => {
          console.log('WebSocket connected');
        };
        ws.onclose = () => {
          console.log('WebSocket closed');
          window.location.reload();
        };
        setWebSocket(ws);
      }

      localStorage.setItem('authToken', JSON.stringify(data));
      navigate('/test');
    } else {
      alert("Hujnia z grzybami");
    }
  };

  const logoutUser = () => {
    setAuthTokens(null);
    setUser(null);
    localStorage.removeItem('authToken');
    navigate('/');
  };

  const updateToken = async () => {
    const response = await fetch('http://localhost:8000/users/token/refresh/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ 'refresh': authToken?.refresh })
    });

    const data = await response.json();

    if (response.status === 200) {
      setAuthTokens(data);
      setUser(jwt_decode(data.access));
      localStorage.setItem('authToken', JSON.stringify(data));
    } else {
      logoutUser();
    }

    if (loading) {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (loading) {
      updateToken();
    }

    const s4Minutes = 1000 * 60 * 4;
    const interval = setInterval(() => {
      if (authToken) {
        updateToken();
      }
    }, s4Minutes);

    return () => clearInterval(interval);
  }, [authToken, loading]);

  return (
    <AuthContext.Provider value={{ user, authToken, loginUser, logoutUser, webSocket }}>
      {loading ? null : children}
    </AuthContext.Provider>
  );
};
