import React, {useState, useEffect, useContext} from 'react'
import AuthContext from '../context/AuthContext'
import { Link } from 'react-router-dom';

const FriendListContext = createContext();

export const FriendList = () => {
    let [friends, setFriends] = useState([])
    let {authToken} = useContext(AuthContext)

    useEffect(() => {
        getFriends()
    }, [])

    let getFriends = async () =>{
        let response = await fetch('http://localhost:8000/users/friends/', {
            method: "GET",
            headers:{
                'Content-Type':'application/json',
                'Authorization':'Bearer ' + String(authToken.access)
            }
        })

        let data = await response.json()
        let new_data = []
        
        // pushing only username's to new array
        for (let i = 0; i < data[0].friends.length; i++) {
            // console.log("test: " + data[0].friends[i].username + " " + i);
            new_data.push(data[0].friends[i].username)
        }
        setFriends(new_data)
    }
    let i = 0

  return (
    <div className='friends_list'>
      <div className='friends_list_title'>Friends list</div>
        {friends.map(f => (
            <Link to={"./mcon/"+f}><div key={f}className='friends_elem'>{f}</div></Link>
        ))}
    </div>
  )
}

export default FriendList
