import React, {useState, useEffect, useContext}  from 'react'
import { w3cwebsocket as W3CWebSocket} from 'websocket';
import {  useParams } from "react-router-dom";
import AuthContext from '../context/AuthContext'


const MessageContainer = () => {
  let { username } = useParams();
  let string = username.toString()

  let [messages, setMessages] = useState([])
  let {chatSocket, authToken, logoutUser} = useContext(AuthContext)

  let message_status_data = null

  let url = `ws://localhost:8000/ws/socket-server` 
  chatSocket = new W3CWebSocket(url + "?token=" + String(authToken.access))

  // functions 'useEffect()' trigges on the first load
  // and every time 'username' is updated 
  useEffect(() => {
    getMessages()
    chatSocket.onopen = () => {
      console.log('WebSocket Client Connected');
    }

    chatSocket.onmessage = (e) =>{
      let data = JSON.parse(e.data)
      console.log('Data:', data)
      if(e.data.type === 'chat'){
        console.log('chat')
      }
    }
  }, [username])
  
  // getting messages form django backend
  let getMessages = async () => {
    let respone = await fetch('http://localhost:8000/users/rooms/'+string, {
      method: 'GET',
      headers:{
        'Content-Type':'application/json',
        'Authorization':'Bearer ' + String(authToken.access)
      }
    }, [])
    
    let data = await respone.json()
    console.log(data)
    setMessages(data)
  }
  
  let sendMessage = async (e) => {
    e.preventDefault()
    chatSocket.send(JSON.stringify({
      'message': 'gdfgfd was sent'
    }))
    let respone = await fetch('http://localhost:8000/users/rooms/send/'+string, {
      method: 'POST',
      headers:{
        'Content-Type':'application/json',
        'Authorization':'Bearer ' + String(authToken.access)
      }, 
      body:JSON.stringify(e.target.message.value)
    })
    document.getElementById('mess').value = ''
    
    // getMessages()
  }


  setTimeout(() => {
      const input = document.getElementById("mess");
      input.focus();
    }, 10);

  return (
    <div className='message_container'>
      <h1>{username}</h1>
      <ul>
        {messages.map(f => (
            <div key={f.id}className='friends_elem'>{f.user.username}: {f.body}</div>
        ))}
      </ul>
      <div className='message_text_input'>
        <form onSubmit={sendMessage}>
          <input type="text" id='mess' name="message" />
          <button type="submit">Send</button>
        </form>
      </div>
      
    </div>
  )
}

export default MessageContainer
