import React, { createContext, useState, useEffect, useContext } from 'react';
import AuthContext from '../context/AuthContext';

const FriendContext = createContext();

export default FriendContext;

export const FriendProvider = ({ children }) => {
  const [friends, setFriends] = useState([]);
  const { authToken, WebSocket } = useContext(AuthContext);

  WebSocket.onmessage = (e) => {
    const data = JSON.parse(e.data);
    if (data.type === "friendRequest") {
      getFriends();
    }
  };

  const getFriends = async () => {
    const response = await fetch('http://localhost:8000/users/friends/', {
      method: "GET",
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer ' + String(authToken.access)
      }
    });

    const data = await response.json();
    const newFriends = data[0].friends.map(friend => friend.username);
    setFriends(newFriends);
  };

  useEffect(() => {
    getFriends();
  }, []);

  const contextData = {
    friends: friends
  };

  return (
    <FriendContext.Provider value={contextData}>
      {children}
    </FriendContext.Provider>
  );
};
